import "./App.css";
import React, { Component } from "react";

class App extends Component {
  state = {
    clients: [
      { id: 0, url: "img/img1.png", text: "Test 1" },
      { id: 1, url: "img/img2.png", text: "Test 2" },
      { id: 2, url: "img/img3.png", text: "Test 3" }
    ],
    pos: {
      current: 0,
      after: 1094,
      befor: -1094
    }
  };

  slide = (nb = 0) => {
    let car = document.getElementsByClassName('container')[0]
    car.style.transform = "translateX(" + nb + "px)";
    this.setState({ current: this.state.pos.befor, after: this.state.pos.befor, befor: this.state.pos.befor + this.state.pos.befor });
  };

  render() {
    return (
      <div class="carousel-wrapper">
        <div class="carousel">
          <ul class="container">
            {this.state.clients.map((item) => (
                <li class="carousel_div" id={item.id}>
                  <div class="flex_item img_container">
                    <img src={item.url} alt={item.text}></img>
                  </div>
                  <div class="flex_item text_container">
                    <p>{item.text}</p>
                  </div>
              </li>
            ))}
          </ul>
        </div>
        <button onClick={() => this.slide(this.state.pos.befor)}></button>
      </div>
    );
  }
}

export default App;
