import "./App.css";
import React, { Component } from "react";

class App extends Component {
  state = {
    clients: [
      { id: 0, url: "../public/img/img1.png", text: "Test 1" },
      { id: 1, url: "%PUBLIC_URL%/img/img2.png", text: "Test 2" },
      { id: 2, url: "%PUBLIC_URL%/img/img3.png", text: "Test 3" }
    ]
  };

  render() {
    return (
      <div class="carousel-wrapper">
        <div class="carousel">
          {this.state.clients.map((item) => (
            <div class="carousel_div" id={item.id}>
              <div class="img_container">
                <img src={item.url} alt={item.text}></img>
              </div>
              <div class="text_container">
                <p>{item.text}</p>
              </div>
            </div>
          ))}
          <div class="arrow prev">
          </div>
          <div class="arrow next">
          </div>
        </div>
      </div>
    );
  }
}

export default App;
